define(['jquery', 'core/modal_factory'], function($, ModalFactory) {

	var init = function() {
		$('.show-comment').on('click', function(e) {
			var clickedLink = $(e.currentTarget);
			ModalFactory.create({
				title: clickedLink.attr("data-comment-title"),
				body: clickedLink.attr("data-comment")
			})
			.then(function(modal) {
				modal.show();
			});
		})
	}

	return {
		init: init
	}
});